package io.quarkiverse.chicory.deployment;

import java.nio.file.Path;
import java.util.List;

import io.quarkus.builder.item.MultiBuildItem;

/**
 * Store data produced by the code generation process for a given Wasm module.
 * <p>
 * An instance of {@link GeneratedWasmClassesBuildItem} contains a reference to the name of a configured Wasm
 * module, a collection of classes that represent the generated bytecode and a reference to the generated meta Wasm.
 * </p>
 */
public final class GeneratedWasmClassesBuildItem extends MultiBuildItem {
    private final String name;
    private final List<Path> classes;
    private final Path metaWasm;
    private final Path javaSources;

    public GeneratedWasmClassesBuildItem(final String name, final List<Path> classes, final Path metaWasm,
            final Path javaSources) {
        this.name = name;
        this.classes = classes;
        this.metaWasm = metaWasm;
        this.javaSources = javaSources;
    }

    public String getName() {
        return name;
    }

    public List<Path> getClasses() {
        return classes;
    }

    public Path getMetaWasm() {
        return metaWasm;
    }

    public Path getJavaSources() {
        return javaSources;
    }

    @Override
    public String toString() {
        return "GeneratedWasmClassesBuildItem{" +
                "name='" + name + '\'' +
                ", metaWasm=" + metaWasm +
                ", javaSources=" + javaSources +
                '}';
    }
}
